C<pre><code>#include &lt;stdio.h&gt;
#include &lt;stdlib.h&gt;

// 定义一个链表节点 
struct Node {
    int data; // 编号
    struct Node* next; // 指针，指向下一个节点 
};

// 创建新节点
struct Node* createNode(int data) {
    struct Node* newNode = (struct Node*)malloc(sizeof(struct Node));
    newNode-&gt;data = data;
    newNode-&gt;next = NULL;
    return newNode;
} 

// 插入节点到链表头部
void insertAtHead(struct Node** head, int data) {
    struct Node* newNode = createNode(data);
    newNode-&gt;next = *head;
    *head = newNode;
}

// 创建循环链表
struct Node* createCircularList(int n) {
    struct Node* head = NULL;
    struct Node* temp;
    for (int i = n; i &gt;= 1; i--) {
        insertAtHead(&amp;head, i);
    }
    temp = head;
    while (temp-&gt;next != NULL) {
        temp = temp-&gt;next;
    }
    temp-&gt;next = head; // 将最后一个节点指向头节点，形成循环链表
    return head;
}

// 删除指定值的节点（适用于循环链表）
void deleteNode(struct Node** head, int key) {
    if (*head == NULL) return;

    struct Node* temp = *head;
    struct Node* prev = NULL;

    // 找到需要删除的节点
    do {
        if (temp-&gt;data == key) {
            if (prev == NULL) { // 删除头节点
                struct Node* tail = *head;
                while (tail-&gt;next != *head) {
                    tail = tail-&gt;next;
                }
                if (tail == *head) { // 只有一个节点
                    free(*head);
                    *head = NULL;
                    return;
                }
                tail-&gt;next = temp-&gt;next;
                *head = temp-&gt;next;
                free(temp);
            } else {
                prev-&gt;next = temp-&gt;next;
                free(temp);
            }
            return;
        }
        prev = temp;
        temp = temp-&gt;next;
    } while (temp != *head);
}

int main() {
    int n, m;
    printf("请输入n和m: ");
    scanf("%d %d", &amp;n, &amp;m);

    struct Node* head = createCircularList(n);

    struct Node* current = head;
    int cnt = 0;
    while (cnt &lt; m &amp;&amp; current != NULL) {
        printf("当前节点编号: %d\n", current-&gt;data);
        current = current-&gt;next;
        cnt++;
    }

    return 0;
}
</code></pre>

