    } 
    temp = head;
    int cnt =0;
    while (temp-&gt;next != NULL){
        temp = temp-&gt;next;
    } 
    temp-&gt;next = head; //循环
    return head; 
}

int main(){
    int n;
    int m;
    printf("请输入n、m:");
    scanf("%d %d",&amp;n,&amp;m);
    int cnt = 1;
    struct Node* head = createCircularList(n);
    struct Node* temp= head;
    struct Node* prev = NULL;
    while (head != head-&gt;next){
        for (cnt =1; cnt&lt;m;cnt++){
            prev = temp;
            temp = temp-&gt;next;
        }
        if (head == temp){
            head = temp-&gt;next;  
        }   
        prev-&gt;next = temp-&gt;next;
        free(temp); 
        temp=prev-&gt;next;
    }
    printf("%d",head-&gt;data);
    return 0;
}
